import React, { useContext, useState, useEffect } from 'react';
import { observer } from 'mobx-react';

import JokeItem from '../joke_item';

import { main_store_ctx } from '../../store/main_store';


export const JokeList = observer(() => {
    const { jokeList } = useContext(main_store_ctx);

    const [heartfill, setheartFill] = useState(false)

    function handlerChangeHeat(){
        setheartFill(!heartfill)
    }
    useEffect(()=>{
        setheartFill(false)
    }, [jokeList])

    if (!jokeList.length) return null;

    return jokeList.map(joke => (
        <JokeItem
            key={joke.id}
            categories={joke.categories}
            updated_at={joke.updated_at}
            id={joke.id}
            value={joke.value}
            heartfill={heartfill}
            handlerChangeHeat={handlerChangeHeat}
        />
    ));
});
